tinymce.PluginManager.add('imgsurfer', function(editor, url) {
    editor.addButton('imgsurfer', {
        text: 'Imagen',
        icon: 'image',
        onclick: function() {
            editor.windowManager.open({
                title: 'Agregar Imagen.',
                width: 500,
                height: 415,
                file: url + "/main.php"
            });
            window.imgsurfer_activeEditor = editor
        }
    })
});